function getLocation(){$.ajax({url:"http://geoip.nekudo.com/api/",type:"POST",dataType:"jsonp",success:function(e){var t=e.location.latitude,n=e.location.longitude,s=SunCalc.getTimes(new Date,t,n);sunrise=parseInt(s.sunrise.getHours()+""+(s.sunrise.getMinutes()<10?"0":"")+s.sunrise.getMinutes()),sunset=parseInt(s.sunset.getHours()+""+(s.sunset.getMinutes()<10?"0":"")+s.sunset.getMinutes()),setTimeout(function(){checkTime()},500),update=setInterval(checkTime,1e3)}})}function checkTime(){var e=new Date,t=parseInt(e.getHours()+""+(e.getMinutes()<10?"0":"")+e.getMinutes()),n=document.body;sunrise>t||t>sunset?n.classList.add("night"):n.classList.remove("night")}var sunrise=[],sunset=[];$(document).ready(function(){document.addEventListener("touchstart",function(){},!0),getLocation()});
